import React, { Component,Fragment } from 'react';
import { Button, Form, FormGroup, Label, Input} from 'reactstrap';

class Form2 extends Component {

constructor(props)
{
    super(props);
    this.state = {
        name: '',
        handleInput : (e) => {
            //logic for state
            console.log(e.value);
        }
    }
}

//functions
// handleInput = (e) => {
//     //logic for state
//     console.log(e.value);
// }

  render() {
    return (
        <Fragment>
        <Form>
        <FormGroup>
          <Label>Box Name</Label>
          <Input onChange={this.handleInput} type="text" name="name" placeholder="Please enter some text" />
        </FormGroup>
        <FormGroup>
          <Label>Width</Label>
          <Input onChange={this.handleInput} type="select" name="width">
            <option>1</option>
            <option>2</option>
            <option>3</option>
            <option>4</option>
            <option>5</option>
          </Input>
          <Label>Background Color</Label>
          <Input onChange={this.handleInput} type="select" name="color">
            <option>red</option>
            <option>green</option>
            <option>blue</option>
            <option>yellow</option>
            <option>pink</option>
          </Input>
          <Label>Text Color</Label>
          <Input onChange={this.handleInput} type="select" name="textColor">
            <option>black</option>
            <option>white</option>
            <option>grey</option>
          </Input>
        </FormGroup>
        <Button onClick={this.handleSubmit}>
            Add Box
        </Button>
      </Form>

      </Fragment>
    );
  }
}

export default Form2;
